import React, { useEffect, useState } from 'react';
import '../styles/registration.css'; // Import your CSS file
import axios from 'axios';

const Registration = () => {



  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    address: '',
    phone: '',
    email: '',
    userRoles: "USER"
  });

  if (!formData.userRoles) {
    formData.userRoles = "USER";
  }

  const [message, setMessage] = useState("");


  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  useEffect(() => {
    setTimeout(() => {
      setMessage("");
    }, 3000);
  }, [message])


  const Register = () => {

    axios.get("http://localhost:6060/users")
      .then(function (response) {
        const users = response.data;
        const existingUser = users.find(user => user.email === formData.email);

        if (existingUser) {
          setMessage("Email is already registered.");
        } else {
          // Proceed with registration
          axios.post(`http://localhost:6060/users`, formData)
            .then(function (response) {
              console.log(response);
              console.log("Registered successful:", response.data);

              setMessage("Your Registration is Successful.");
              setFormData({
                firstName: '',
                lastName: '',
                address: '',
                phone: '',
                password: '',
                email: '',
                userRoles: "USER", 
              });
            })
            .catch(function (error) {
              console.log("Error:", error);
              if (error.response) {
                console.log("Server responded with:", error.response.data);
                setMessage("Please fill the correct data.");
              }
            });
        }
      })
      .catch(function (error) {
        console.log(error);
      });
  }



  const handleSubmit = (event) => {
    event.preventDefault();
    // You can perform form submission or validation here
    console.log(formData);
  };

  return (
    <div className="registration-container">
      <h2>Vehicle Management System Registration</h2>
      <form className="registration-form" onSubmit={handleSubmit}>
        <input
          type="text"
          name="firstName"
          placeholder="First Name"
          value={formData.firstName}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="lastName"
          placeholder="Last Name"
          value={formData.lastName}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="address"
          placeholder="Address"
          value={formData.address}
          onChange={handleChange}
          required
        />
        <input
          type="tel"
          name="phone"
          placeholder="Mobile"
          value={formData.phone}
          onChange={handleChange}
          pattern="[0-9]{10}"
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
          minLength={6}
          required
        />
        <button type="submit" onClick={Register}>Register</button>
      </form>
      <div><h4> {message}</h4></div>
    </div>

  );
};

export default Registration;
