import React, { useEffect, useState } from "react";
import "../../styles/booking-form.css";
import { Form, FormGroup } from "reactstrap";
import axios from "axios";
import { useLocation } from "react-router-dom";

const BookingForm = () => {
  const location = useLocation();
  const selectedCar = location.state?.car || {};

  const [cars, setCars] = useState([]); 
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    mobile: "",
    address: ""
  });

  useEffect( () => {
    loadCars();
  }, []);

  const loadCars = () => {
    axios.get('http://localhost:6060/cars')
    .then(function (response) {
      setCars(response.data);
      console.log(response.data);
    })
    .catch(function (error) {

      console.log(error);
    })
    .finally(function () {

    });
  }
  const handleInputChange=(args)=>{
    var copyOfFormData= 
            {...formData};
            copyOfFormData[args.target.name] = 
            args.target.value;
    setFormData(copyOfFormData);
}


  // Create a new booking object combining selectedCar and formData
  const bookingData = {
    car: selectedCar,
    customer: formData
  };

  const Submit = () => {
    // Send the bookingData to the backend
  axios.post('http://localhost:6060/bookings', bookingData)
  .then(function (response) {
    console.log("Booking successful:", response.data);
    // Handle any further actions or notifications
  })
  .catch(function (error) {
    console.log("Error:", error);
    // Handle errors or display error messages
  });
  }


  const submitHandler = (event) => {
    event.preventDefault();
  };
  return (
    <Form onSubmit={submitHandler}>
      <FormGroup className="booking__form d-inline-block me-4 mb-4">
        <input type="text" placeholder="Brand Name" 
          value={selectedCar.brandName || ""}
          disabled
        />
      </FormGroup>
      <FormGroup className="booking__form d-inline-block me-4 mb-4">
        <input type="text" placeholder="Model Name" 
          value={selectedCar.modelName || ""}
          disabled
        />
      </FormGroup>
      <FormGroup className="booking__form d-inline-block me-4 mb-4">
        <input type="text" name="firstName" placeholder="First Name"
        value={formData.firstName}
        onChange={handleInputChange} />

      </FormGroup>
      <FormGroup className="booking__form d-inline-block me-4 mb-4">
        <input type="text" name="lastName" placeholder="Last Name" 
        value={formData.lastName}
        onChange={handleInputChange}
        />
      </FormGroup>
      <FormGroup className="booking__form d-inline-block me-4 mb-4">
        <input type="text" name="email" placeholder="Email"
        value={formData.email}
        onChange={handleInputChange} />
      </FormGroup>
      <FormGroup className="booking__form d-inline-block ms-1 mb-4">
        <input type="number" name="mobile" placeholder="Phone Number" 
        value={formData.mobile}
        onChange={handleInputChange}/>
      </FormGroup>
      <FormGroup className="booking__form d-inline-block ms-1 mb-4 ">
        <input type="text" name="address" placeholder="Address"
        value={formData.address}
        onChange={handleInputChange}
         />
      </FormGroup>

      <button className="mySubmit btn btn-outline-primary" onClick={Submit}> 
            Submit
      </button>



    </Form>
  );
};

export default BookingForm;
